CmdLine:
mkdir -p /var/run/php 	&& sed -i "s|BINARY_LOCATION|"$(find /usr/sbin/ -name php-fpm*)"|g" /etc/supervisor/conf.d/php-fpm.conf 	&& sed -i "s|CONFIG_LOCATION|"$(find /etc/php -name www.conf)"|g" /etc/supervisor/conf.d/php-fpm.conf 	&& sed -i '/#location/,/}/d' /etc/nginx/sites-enabled/default 	&& sed -i 's/# pass PHP scripts to FastCGI server/# pass PHP scripts to FastCGI server
        location ~ \.php$ {
                include snippets/fastcgi-php.conf;
                fastcgi_pass unix:'$(cat /etc/php/*/fpm/pool.d/www.conf  |egrep "^listen =" | awk '{print $3}' | sed 's///\//g')';
        }/g' /etc/nginx/sites-enabled/default 	&& sed -i 's/index.nginx-debian.html/index.php index.nginx-debian.html/g' /etc/nginx/sites-enabled/default 	&& apt-get -y autoremove 	&& apt-get clean autoclean 	&& rm -rf /var/lib/{log,dpkg,apt,cache}
